.container {
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  font-size: 16px;
  background-color: white;

  & .title {
    justify-content: center;
    align-items: center;
    background-color: aquamarine;
    font-size: 30px;
  }

  & .row {
    width: 80vw;
    min-width: 500px;
    height: 200px;
    margin: 20px;
    display: flex;
    justify-content: center;
    background-color: white;

    & .forms {
      border: 1px solid lightgreen;
      border-radius: 10px;
      width: 400px;
      height: 200px;
      margin: 0 20px;
      display: flex;
      flex-direction: column;
      justify-content: center;
      align-items: center;

      & button {
        box-shadow: 0 10px 14px -7px #3e7327;
        background: linear-gradient(to bottom, #77b55a 5%, #72b352 100%);
        background-color: #77b55a;
        border-radius: 4px;
        border: 1px solid #4b8f29;
        display: inline-block;
        cursor: pointer;
        color: white;
        font-size: 16px;
        font-weight: bold;
        padding: 6px 19px;
        text-decoration: none;
        text-shadow: 0 0 0 #5b8a3c;
      }

      & button:hover {
        background: linear-gradient(to bottom, #72b352 5%, #77b55a 100%);
        background-color: #72b352;
      }

      & button:active {
        position: relative;
        top: 1px;
      }
    }

    & .forms:last-child {
      border: 1px solid salmon;
    }

    & .icons {
      width: 200px;
      height: 200px;
      display: flex;
      flex-direction: column;
      justify-content: center;
      align-items: center;

      & .budget-amount {
        font-size: 32px;
        color: lightgreen;
      }

      & .expense-amount {
        font-size: 32px;
        color: lightcoral;
      }

      & .balance-amount {
        font-size: 32px;
      }
    }

    & .expenses {
      margin: 0 20px;
    }

    & .history {
      display: flex;
      justify-content: space-around;
      overflow: scroll;
      width: 700px;

      background-color: coral;
    }
  }
}

.green {
  color: lightgreen;
}

.red {
  color: coral;
}
